;-----------------------------------------------------------------------------
;  FUNCTION NAME: _RECEIVE_ISR
;
;  DESCRIPTION: Unless modified, this implements only a null handler stub.
;
;-----------------------------------------------------------------------------
;

_RECEIVE_ISR:

   ;@PSoC_UserCode_BODY@ (Do not change this line.)
   ;---------------------------------------------------
   ; Insert your custom code below this banner
   ;---------------------------------------------------
   ;   NOTE: interrupt service routines must preserve
   ;   the values of the A and X CPU registers.

   ;---------------------------------------------------
   ; Insert your custom code above this banner
   ;---------------------------------------------------
   ;@PSoC_UserCode_END@ (Do not change this line.)

 IF (RECEIVE_RXBUF_ENABLE)
   push A
   push X

   IF SYSTEM_LARGE_MEMORY_MODEL
      REG_PRESERVE IDX_PP                                  ; Save the IDX_PP register	
   ENDIF

   mov  X,[RECEIVE_bRxCnt]                                 ; Load X with byte counter
   mov  A,REG[RECEIVE_CONTROL_REG]                         ; Read the control register
   push A                                                  ; Store copy for later test
                                                           ; IF real RX interrupt
   and  A,RECEIVE_RX_REG_FULL                              ; Did really really get an IRQ
   jnz  .UARTRX_ReadRx                                     ; Data ready, go get it
   pop  A                                                  ; Restore stack
   jmp  .RESTORE_IDX_PP

.UARTRX_ReadRx:

   pop  A                                                  ; Restore status flags
                                                           ; IF there is no error, get data
                                                           ; Check for parity or framing error
   and  A,RECEIVE_RX_ERROR
   jz   .UARTRX_NO_ERROR                                   ; If there is not an Error go read data

   or   [RECEIVE_fStatus],A                                ; Set error flags (parity,framing,overrun) bits

   tst  REG[RECEIVE_RX_BUFFER_REG], 0x00                   ; Read the data buffer to clear it.

   and  A,RECEIVE_RX_FRAMING_ERROR                         ; Check for framing error special case
   jz   .RESTORE_IDX_PP                                    ; Not framing error, all done

                                                           ; Disable and re-enable RX to reset after
                                                           ; framing error.
   and   REG[RECEIVE_CONTROL_REG], ~RECEIVE_RX_ENABLE      ; Disable RX
   or    REG[RECEIVE_CONTROL_REG],  RECEIVE_RX_ENABLE      ; Enable RX
   jmp  .RESTORE_IDX_PP                                    ; Done with framing error, leave.


.UARTRX_NO_ERROR:
   mov  A,REG[RECEIVE_RX_BUFFER_REG ]                      ; Read the data buffer

                                                           ; IF buffer not full
   tst  [RECEIVE_fStatus],RECEIVE_RX_BUF_CMDTERM           ; Check for buffer full
   jnz  .RESTORE_IDX_PP                                    ; All done

   cmp  A,RECEIVE_CMD_TERM                                 ; Check for End of command
   jnz  .UARTRX_CHK_CTLCHAR
   or   [RECEIVE_fStatus],RECEIVE_RX_BUF_CMDTERM           ; Set command ready bit

   RAM_SETPAGE_IDX >RECEIVE_aRxBuffer
   RAM_CHANGE_PAGE_MODE FLAG_PGMODE_10b
   mov  [X + RECEIVE_aRxBuffer],00h                        ; Zero out last data
   RAM_CHANGE_PAGE_MODE FLAG_PGMODE_00b
   jmp  .RESTORE_IDX_PP

.UARTRX_CHK_CTLCHAR:                                       ; Ignore charaters below this value
                                                           ; If ignore char is set to 0x00, do not
                                                           ; ignore any characters.
IF(RECEIVE_RX_IGNORE_BELOW)
   cmp  A,RECEIVE_RX_IGNORE_BELOW
   jc   .RESTORE_IDX_PP
ENDIF

.UARTRX_CHK_OVFL:                                          ; Check for MAX String here
   cmp  [RECEIVE_bRxCnt],(RECEIVE_RX_BUFFER_SIZE - 1)
   jc   .UARTRX_ISR_GETDATA
   or   [RECEIVE_fStatus],RECEIVE_RX_BUF_OVERRUN           ; Set error flags (parity,framing,overrun) bits

   RAM_SETPAGE_IDX >RECEIVE_aRxBuffer             ;   using idexed address mode
   RAM_CHANGE_PAGE_MODE FLAG_PGMODE_10b
   mov  [X + RECEIVE_aRxBuffer],00h                        ; Zero out last data
   RAM_CHANGE_PAGE_MODE FLAG_PGMODE_00b
   jmp  .RESTORE_IDX_PP

                                                           ; IF input data == "CR", then end of command
.UARTRX_ISR_GETDATA:
   inc  X                                                  ; Inc the pointer
   mov  [RECEIVE_bRxCnt],X                                 ; Restore the pointer
   dec  X                                                  ; Mov X to its original value

   RAM_SETPAGE_IDX >RECEIVE_aRxBuffer             ;   using idexed address mode
   RAM_CHANGE_PAGE_MODE FLAG_PGMODE_10b
   mov  [X+RECEIVE_aRxBuffer],A                            ; store data in array
   RAM_CHANGE_PAGE_MODE FLAG_PGMODE_00b


.RESTORE_IDX_PP:
   IF SYSTEM_LARGE_MEMORY_MODEL
      REG_RESTORE IDX_PP
   ENDIF

.END_UARTRX_ISR:
   pop  X
   pop  A

ENDIF

RECEIVE_RX_ISR_END:
   reti


